/**
 * 渲染 osu! 成绩格式的图片
 *
 *
 * @param content - 需要渲染的内容，为 osu!api 直接返回内容，可参考{@link https://osu.ppy.sh/docs/index.html#get-user-scores}
 * 
 * @returns Promise<string>，返回值为结果图片的 Gokapi 的直链
 * 
 */
export function renderScore(content: any): Promise<string> {
  return new Promise(async (resolve) => {
    nodeHtmlToImage({
      html: await getHTML(content),
      type: "png",
      encoding: "binary",
      transparent: true
    })
      .then(async (buffer) => {
        uploadToGokapi(buffer as Buffer, `kbot-reply-score-${randomBytes(5).toString('hex')}.png`, 1, 0)
          .then((res) => {
            if (res.hotlinkUrl) {
              resolve(res.hotlinkUrl)
            } else {
              log.error(`ERROR: renderScore: empty hotlinkUrl from Gokapi!`)
            }
          })
      })
      .catch((error) => {
        log.error(`ERROR: renderScore: ${error.toString()}`)
      })
  })
}